/*
Script d'initialisation des tables utilisées pour la formation SQL de Cap Numérique
*/
DROP TABLE IF EXISTS images;
DROP TABLE IF EXISTS adhesions;
DROP TABLE IF EXISTS groupes;
DROP TABLE IF EXISTS messages;
DROP TABLE IF EXISTS personnes;

CREATE TABLE personnes (id_personne INTEGER PRIMARY KEY AUTOINCREMENT, nom VARCHAR(30) NOT NULL, pseudo VARCHAR(30) NOT NULL, sexe VARCHAR(5), age INTEGER, taille DECIMAL(3,2), poids SMALLINT, email VARCHAR(30), ville VARCHAR(50), hobby VARCHAR(50));
CREATE TABLE messages (id_message INTEGER PRIMARY KEY AUTOINCREMENT, id_personne INTEGER NOT NULL, message VARCHAR(500) NOT NULL);
CREATE TABLE groupes (id_groupe INTEGER PRIMARY KEY AUTOINCREMENT, nom VARCHAR(100) NOT NULL, description VARCHAR(250));
CREATE TABLE adhesions (id_groupe INTEGER NOT NULL, id_personne INTEGER NOT NULL, date_adhesion DATE NOT NULL);
CREATE TABLE images (id_image INTEGER PRIMARY KEY AUTOINCREMENT, id_personne INTEGER NOT NULL, url_fichier VARCHAR(250) NOT NULL, hauteur INTEGER, largeur INTEGER);





